import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import { removeToken } from '../utils/auth';
import '../styles/Sidebar.css';

const navItems = [
  { path: '/', label: 'Dashboard', icon: 'üìä' },
  { path: '/owners', label: 'Owners', icon: 'üë§' },
  { path: '/restaurants', label: 'Restaurants', icon: 'üçΩÔ∏è' },
  { path: '/customers', label: 'Customers', icon: 'üßë‚Äçü§ù‚Äçüßë' },
  { path: '/bookings', label: 'Bookings', icon: 'üìÖ' },
  { path: '/analytics', label: 'Analytics', icon: 'üìà' },
  { path: '/settings', label: 'Settings', icon: '‚öôÔ∏è' }
];

function Sidebar() {
  const navigate = useNavigate();
  function handleLogout() {
  removeToken();
  // do a full navigation to ensure the app loads the login route immediately
  window.location.href = '/login';
  }

  return (
    <aside className="sidebar">
      <div className="sidebar-header">Admin</div>
      <nav className="sidebar-nav">
        {navItems.map(item => (
          <NavLink
            key={item.path}
            to={item.path}
            className={({ isActive }) => isActive ? 'nav-link active' : 'nav-link'}
            end={item.path === '/'}
          >
            <span className="nav-icon">{item.icon}</span>
            <span>{item.label}</span>
          </NavLink>
        ))}
      </nav>

      <div className="sidebar-footer">
        <button className="small-btn" onClick={handleLogout}>Logout</button>
      </div>
    </aside>
  );
}

export default Sidebar;
