import React, { createContext, useContext, useState, useCallback } from 'react';
import Toast from './Toast';

const ToastContext = createContext();

export function ToastProvider({ children }) {
  const [toast, setToast] = useState(null);
  const show = useCallback((msg, ms = 3000, actionLabel = null, onAction = null) => {
    setToast({ msg, actionLabel, onAction });
    if (ms > 0) setTimeout(() => setToast(null), ms);
  }, []);
  return (
    <ToastContext.Provider value={{ show }}>
      {children}
      <Toast message={toast?.msg} actionLabel={toast?.actionLabel} onAction={toast?.onAction} onClose={() => setToast(null)} />
    </ToastContext.Provider>
  );
}

export function useToast() {
  return useContext(ToastContext);
}
