import React, { useState } from 'react';
import './Footer.css';
import Button from './common/Button';

export default function Footer() {
  const [email, setEmail] = useState('');
  const [subscribed, setSubscribed] = useState(false);

  const subscribe = (e) => {
    e.preventDefault();
    if (!email || !email.includes('@')) return alert('Please enter a valid email');
    setSubscribed(true);
  };

  return (
    <footer className="site-footer">
      <div className="footer-top">
        <div className="footer-section newsletter">
          <h3>Subscribe to our newsletter</h3>
          <p style={{ marginBottom: '1rem', color: 'var(--text-secondary)' }}>Get updates on latest offers and restaurants.</p>
          <form onSubmit={subscribe} className="newsletter-form">
            <input
              type="email"
              placeholder="Your email"
              value={email}
              onChange={e => setEmail(e.target.value)}
            />
            <Button type="submit" variant="primary" size="medium">
              {subscribed ? 'Subscribed' : 'Subscribe'}
            </Button>
          </form>
        </div>

        <div className="footer-section socials">
          <h4>Follow us</h4>
          <div className="social-icons">
            <a href="#" aria-label="facebook" className="soc">ðŸ”µ</a>
            <a href="#" aria-label="twitter" className="soc">ðŸ”·</a>
            <a href="#" aria-label="instagram" className="soc">ðŸŸ£</a>
          </div>
        </div>

        <div className="footer-section footer-links">
          <h4>Quick Links</h4>
          <ul>
            <li><a href="/dashboard">Home</a></li>
            <li><a href="/restaurants">Restaurants</a></li>
            <li><a href="/offers">Offers</a></li>
            <li><a href="#">Contact</a></li>
          </ul>
        </div>

        <div className="footer-section contact">
          <h4>Contact</h4>
          <p style={{ color: 'var(--text-secondary)' }}>support@restro.com</p>
          <p style={{ color: 'var(--text-secondary)' }}>+1 (555) 123-4567</p>
        </div>
      </div>

      <div className="footer-bottom">
        <p>Â© {new Date().getFullYear()} Your Restro. All rights reserved.</p>
      </div>
    </footer>
  );
}
