import React, { useEffect, useState } from 'react';
import '../../styles/Toast.css';

export default function Toast({ message, actionLabel, onAction, onClose }) {
  const [visible, setVisible] = useState(false);
  useEffect(() => {
    if (message) {
      setVisible(true);
    } else {
      setVisible(false);
    }
  }, [message]);
  if (!message) return null;
  return (
    <div className={`app-toast ${visible ? 'in' : 'out'}`}>
      <div style={{display:'flex',alignItems:'center',gap:12}}>
        <div style={{flex:1}}>{message}</div>
        {actionLabel && <button className="btn-secondary small-btn" onClick={() => { if (onAction) onAction(); if (onClose) onClose(); }}>{actionLabel}</button>}
      </div>
    </div>
  );
}
