import React, { useState } from 'react';
import RestaurantCard from '../components/RestaurantCard';
import '../styles/HomePage.css';

const mockRestaurants = [
  {
    id: 1,
    name: 'Green Leaf Bistro',
    location: 'Downtown',
    cuisine: 'Italian',
    image: '/assets/greenleaf.jpg',
    rating: 4.7,
    details: 'Fresh, organic, and delicious Italian cuisine.'
  },
  {
    id: 2,
    name: 'Teal Table',
    location: 'Uptown',
    cuisine: 'Modern European',
    image: '/assets/tealtable.jpg',
    rating: 4.5,
    details: 'Elegant dining with a modern twist.'
  },
  // ...more mock data
];

function HomePage() {
  const [search, setSearch] = useState('');
  const filtered = mockRestaurants.filter(r =>
    r.name.toLowerCase().includes(search.toLowerCase()) ||
    r.location.toLowerCase().includes(search.toLowerCase()) ||
    r.cuisine.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="homepage">
      <div className="hero-section">
        <h1>Find & Book Your Table</h1>
        <input
          className="search-bar"
          type="text"
          placeholder="Search by name, location, cuisine..."
          value={search}
          onChange={e => setSearch(e.target.value)}
        />
      </div>
      <div className="restaurant-grid">
        {filtered.map(r => (
          <RestaurantCard key={r.id} restaurant={r} />
        ))}
      </div>
    </div>
  );
}

export default HomePage;
