import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { authFetch } from '../utils/auth';

function RestaurantManage() {
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [menu, setMenu] = useState([]);
  const [tables, setTables] = useState([]);

  useEffect(()=>{
    authFetch(`/api/owner/restaurants/${id}`).then(setRestaurant).catch(()=>setRestaurant(null));
    authFetch(`/api/owner/menu/restaurant/${id}`).then(setMenu).catch(()=>setMenu([]));
    authFetch(`/api/owner/tables/restaurant/${id}`).then(setTables).catch(()=>setTables([]));
  },[id]);

  if (!restaurant) return <div>Loading...</div>;

  return (
    <div>
      <h2>Manage: {restaurant.name}</h2>
      <div style={{display:'flex',gap:16}}>
        <div style={{flex:1}}>
          <h3>Menu</h3>
          <Link to={`/menu/new?restaurantId=${id}`} className="btn-primary">Add Menu Item</Link>
          <div>
            {menu.map(m=> (
              <div key={m._id} style={{padding:8,borderBottom:'1px solid #eee'}}>
                <strong>{m.name}</strong> — ${m.price}
              </div>
            ))}
          </div>
        </div>
        <div style={{flex:1}}>
          <h3>Tables</h3>
          <Link to="/tables/new" className="btn-primary">Add Table</Link>
          <div>
            {tables.map(t=> (
              <div key={t._id} style={{padding:8,borderBottom:'1px solid #eee'}}>
                <strong>{t.name}</strong> — capacity {t.capacity}
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default RestaurantManage;
