import React, { useEffect, useState } from 'react';
import { authFetch } from '../utils/auth';

function MenuList() {
  const [items, setItems] = useState([]);

  useEffect(() => {
    // naive: fetch first restaurant then its menu
    authFetch('/api/owner/restaurants').then(rests => {
      if (!rests.length) return setItems([]);
      authFetch(`/api/owner/menu/restaurant/${rests[0]._id}`).then(setItems).catch(console.error);
    }).catch(console.error);
  }, []);

  return (
    <div>
      <h2>Your Menu Items (first restaurant)</h2>
      <div className="menu-grid">
        {items.map(i => (
          <div key={i._id} className="menu-card">
            {i.image ? <img src={i.image} alt="item" className="menu-image"/> : null}
            <div className="menu-body">
              <strong>{i.name}</strong>
              <div className="muted">${i.price}</div>
              <div className="small muted">{i.description}</div>
              <div className="card-actions">
                <button className="small-btn">Edit</button>
                <button className="small-btn danger-btn">Delete</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default MenuList;
